        if (keyCode !== 13) return
        const todoObj={id:nanoid(),name:target.value,done:false}
        this.props.addTodo(todoObj)
    }*/

    render() {

        return (
            <div className="todo-header">
                <input onKeyUp={this.keyupHander} type="text" placeholder="请输入你的任务名称，按回车键确认"/>
            </div>
        );
    }
}
